package practice;

public class 문제8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10, num2=10;
	    System.out.printf("%d \n", ++num1); // 증가 후 출력 ==>11
	    System.out.printf("%d \n", num1); //11
	    System.out.printf("%d \n", num2++); // 출력 후 증가 ==>10
	    System.out.printf("%d \n", num2); //11


	}

}
